mapper.insert:
INSERT INTO CLASS_REGIST(REGIST_NO
                , REGIST_PAYCODE
                , REGIST_PRICE
                , REGIST_PERSON_NO
                , REGIST_DATE
                , MEM_NO
                , CLASS_NO)
           VALUES(#{registNo}
                , #{registPaycode}
                , #{registPrice}
                , #{registPersonNo}
                , SYSDATE
                , #{memNo}
                , #{classNo})

mapper.delete:
DELETE 
  FROM CHAT_MEM
 WHERE CLASS_NO = #{classNo}
   AND MEM_NO = #{memNo}
